"use strict";function init(){getScrollButton(),toggleTab()}function getScrollButton(){for(var n=document.querySelectorAll('a[href^="#"]'),t=0;t<n.length;t++)!function(e){n[e].addEventListener("click",function(t){t.preventDefault();t=n[e].getAttribute("href"),t=+(document.getElementById(t.replace("#","")).getBoundingClientRect().top+window.pageYOffset);window.scrollTo({top:t,behavior:"smooth"})})}(t)}function toggleTab(){for(var r=document.querySelectorAll(".js-tab-button"),i=document.querySelectorAll(".js-tab-block"),t=0;t<r.length;t++)!function(o){r[o].addEventListener("click",function(){for(var t=0;t<r.length;t++)r[t].setAttribute("aria-selected",!1);r[o].setAttribute("aria-selected",!0);for(var e=0;e<i.length;e++)i[e].setAttribute("aria-hidden",!0);var n=r[o].getAttribute("aria-controls");document.getElementById(n).setAttribute("aria-hidden",!1)})}(t)}init();